// Utility functions
export function getServiceUrl(service: ServiceConfig, endpoint: string = ''): string {
  return `http://${service.host}:${service.port}${endpoint}`;
}

export function getHealthUrl(service: ServiceConfig): string {
  return service.healthEndpoint ? getServiceUrl(service, service.healthEndpoint) : getServiceUrl(service);
}

export function getDocsUrl(service: ServiceConfig): string {
  return service.docsEndpoint ? getServiceUrl(service, service.docsEndpoint) : getServiceUrl(service);
}

// Convenience functions
export function getHecateConfig(): ServiceConfig {
  return config.hecateAgent;
}

export function getAgentsConfig(): ServiceConfig {
  return config.generalAgents;
}

export function getMcpConfig(): ServiceConfig {
  return config.mcpServer;
}

export function getFrontendConfig(): ServiceConfig {
  return config.frontend;
}

export function getAllServiceUrls(): Record<string, string> {
  return {
    VITE_MCP_API_URL: getServiceUrl(config.mcpServer),
    VITE_EREBUS_API_URL: getServiceUrl(config.erebus),
    VITE_ORCHESTRATION_API_URL: getServiceUrl(config.orchestration),
    VITE_AGENTS_API_URL: getServiceUrl(config.generalAgents),
    VITE_HECATE_API_URL: getServiceUrl(config.hecateAgent),
  };
}

